var dropAreaPos = document.getElementById("drop-area-pos");

function dropPos(e) {
    e.preventDefault();
    var r = e.dataTransfer.files[0];
    const a = new FileReader;
    a.addEventListener("load", (e => {
        let r = e.target.result;
        parseToArrays(r, simulateObject), simulateObject.simulateByData(), $("#file-spins").trigger("upload-pos")
    })), a.readAsText(r)
}

function dragover(e) {
    e.preventDefault(), this.classList.add("dragover")
}

function dragleave(e) {
    e.preventDefault(), this.classList.remove("dragover")
}
dropAreaPos.addEventListener("drop", dropPos), dropAreaPos.addEventListener("dragover", dragover), dropAreaPos.addEventListener("dragleave", dragleave);